export default (filename) => {
  const uniqueName = uuid().split('-')[0];
  const mimeType = filename.slice(filename.lastIndexOf('.') + 1);
  return `image_${uniqueName}.${mimeType}`;
};

const statusRes = {
  200: 'success',
  401: 'Authentication error',
  201: 'success',
  400: 'error',
  403: 'error, not allowed',
  404: 'Error, not found'
};

const errorResponse = (res, status, message) => {
  res.status(status).json({ status, error: message });
};

const successResponse = (res, status, message = null, payload = null) => {
  res.status(status).json({
    status: statusRes[status],
    data: {
      message,
      ...payload
    }
  });
};

const successResponseArray = (res, status, arrayData) => {
  const response = {
    status: statusRes[status],
    data: arrayData
  };
  res.status(status).json(response);
};

export { errorResponse, successResponse, successResponseArray };

import pool from '../index';
import { getColumns } from '../../utiities/db_utils/queryUtils';


// Add item to a table
export const createItem = async (table, data) => {
	const values = Object.values(data);
	const keys = Object.keys(data).map(val => `"${val}"`);
	const columns = getColumns(keys);
	const query = {
		text: `INSERT INTO ${table}(
      ${keys.toString()}
    ) VALUES(${columns}) RETURNING *`,
		values
	};
	
	try {
		const { rows } = await pool.query(query)
		return { error: null, result: rows[0] };
	} catch (error) {
		return { error: error.message, result: null };
	}
};


// Update a single item
export const updateItem = async (table, id, data) => {
	const values = Object.values(data);
	const keys = Object.keys(data).map(val => `"${val}"`);
	const columns = getColumns(keys);
	const query = {
		text: `UPDATE ${table} SET (${keys.toString()}) = (${columns}) WHERE id='${id}' RETURNING *`,
		values,
	};
	try {
		const { rows } = await pool.query(query);
		return { error: null, result: rows[0] };
	} catch (error) {
		return { error: error.message };
	}
};

// Delete an item
export const deleteItem = async (table, id) => {
	const query = {
		text: `DELETE FROM ${table} WHERE id=$1 `,
		values: [id]
	};
	try {
		const { rowCount } = await pool.query(query);
		return { error: null, result: rowCount };
	} catch (error) {
		return { error: error.message };
	}
};

// Get a single item from db, expect a single key value pair option
export const getItem = async (table, option) => {
	const value = Object.values(option);
	const key = Object.keys(option)[0];
	const query = {
		text: `SELECT * FROM ${table} WHERE ${key}=$1`,
		values: value
	};
	try {
		const { rows } = await pool.query(query);
		return { error: null, result: rows[0] };
	} catch (error) {
		return { error: error.message };
	}
};

// Get items froom the database
export const getItems = async (table, condition = null) => {
	const value = condition ? Object.values(condition) : null;
	const key = condition ? Object.keys(condition).toString() : null;
	const query = !condition
		? { text: `SELECT * FROM ${table}` }
		: {
			text: `SELECT * FROM ${table} WHERE "${key}"=$1 `,
			values: value
		};
	try {
		const { rows } = await pool.query(query);
		return { error: null, result: rows };
	} catch (error) {
		return { error: error.message };
	}
};

